mapper.select:
SELECT 
FROM DATA_CLIENT C

WHERE C.DEL_FLAG='0' AND A.DEL_FLAG='0' AND O.DEL_FLAG='0' AND C.CLIENT_NAME = #{clientName}

SELECT 
FROM DATA_CLIENT C


    C.DEL_FLAG='0' AND A.DEL_FLAG='0' AND O.DEL_FLAG='0'

        C.CLIENT_NAME = #{clientName}


        C.ORGANIZATION = #{organization.id}


        C.AREA = #{area.id}


ORDER BY C.UPDATE_TIME ASC
