mapper.select:
SELECT C.AREA_NAME AS "area", SUM(N_DRUGAMOUNT) AS "amount", SUM(N_DRUGCOUNT) AS "count" FROM (SELECT DI.N_DRUGCOUNT, DI.N_DRUGAMOUNT, DI.C_COMMONNAME, A.AREA_NAME FROM T_SIIP_DRUGINSTOREDETAIL DI LEFT JOIN DRUG_AREA A ON A.ID = DI.AREA
DI.CREATETIME >= #{startDate} AND DI.CREATETIME <= #{endDate}
AND DI.C_COMMONNAME=#{drugName}
) C GROUP BY C.AREA_NAME ORDER BY SUM(N_DRUGAMOUNT)

SELECT C.AREA_NAME AS "area", SUM(N_DRUGAMOUNT) AS "amount", SUM(N_DRUGCOUNT) AS "count" FROM (SELECT DO.N_DRUGCOUNT, DO.N_DRUGAMOUNT, DO.C_COMMONNAME, A.AREA_NAME FROM T_SIIP_DRUGOUTSTOREDETAIL DO LEFT JOIN DRUG_AREA A ON A.ID = DO.AREA
DO.CREATETIME >= #{startDate} AND DO.CREATETIME <= #{endDate}
AND DO.C_COMMONNAME=#{drugName}
) C GROUP BY C.AREA_NAME ORDER BY SUM(N_DRUGAMOUNT)
