mapper.update:
UPDATE DRUG_ORGANIZATION SET
DEL_FLAG = '1'
WHERE ID=#{id}

mapper.select:
SELECT 
FROM DRUG_ORGANIZATION O

    O.DEL_FLAG = '0'

        AND  O.ORGAN_CODE = #{areaCode}


        AND  O.ORGAN_NAME = #{organName}


        AND O.ORGAN_ENAME = #{organEname}


ORDER BY O.UPDATE_TIME ASC
